mapper.select:
SELECT *
FROM client a
WHERE a.client_id = #{id}

select m.medicament_name AS [Medicament], o.order_date AS [Order date], c.client_id AS [Client id], c.client_full_name AS [Client], c.client_phone AS [Client phone]
FROM [order] o JOIN [order_medicament] om ON o.order_id = om.order_id JOIN [medicament] m ON m.medicament_id = om.medicament_id JOIN [prescription] pres ON pres.prescription_id = om.prescription_id JOIN [client] c ON pres.client_id = c.client_id
WHERE LOWER(m.medicament_name) = LOWER(dbo.DeleteDoublуSpace(#{name})) and o.order_date between #{firstDate} and #{secondDate}

select m.medicament_name AS [Medicament], o.order_date AS [Order date], c.client_id AS [Client id], c.client_full_name AS [Client], c.client_phone AS [Client phone]
FROM [order] o JOIN [order_medicament] om ON o.order_id = om.order_id JOIN [medicament] m ON m.medicament_id = om.medicament_id JOIN [prescription] pres ON pres.prescription_id = om.prescription_id JOIN [client] c ON pres.client_id = c.client_id
WHERE LOWER(m.medicament_type) = LOWER(dbo.DeleteDoublуSpace(#{type})) and o.order_date between #{firstDate} and #{secondDate}
